import React from 'react';

const Hero = () => {
  return (
    <section className="flex flex-col items-center justify-center min-h-screen bg-black text-white">
      <div className="text-center">
        <h1 className="text-5xl font-extrabold mb-4 text-purple-500">Welcome to Our SaaS Platform</h1>
        <p className="text-xl mb-8 text-gray-400">Effortlessly manage your business with our powerful tools.</p>
        <button className="px-6 py-3 text-lg font-semibold text-black bg-purple-500 rounded-full hover:bg-purple-400 transition duration-300">
          Get Started
        </button>
      </div>
      <div className="mt-8">
        <img src="your-image-url.jpg" alt="SaaS Platform" className="rounded-lg shadow-lg max-w-md mx-auto" />
      </div>
    </section>
  );
};

export default Hero;
