import React from 'react';

const Features = () => {
  return (
    <section className="bg-gray-100 py-16">
      <div className="max-w-4xl mx-auto text-center">
        <h2 className="text-3xl font-bold mb-8">Our Features</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          <div className="flex flex-col items-center">
            <img src="feature-image-1.jpg" alt="Feature 1" className="mb-4 rounded-lg shadow-lg" />
            <p className="text-lg">Feature 1 Description</p>
          </div>
          <div className="flex flex-col items-center">
            <img src="feature-image-2.jpg" alt="Feature 2" className="mb-4 rounded-lg shadow-lg" />
            <p className="text-lg">Feature 2 Description</p>
          </div>
          <div className="flex flex-col items-center">
            <img src="feature-image-3.jpg" alt="Feature 3" className="mb-4 rounded-lg shadow-lg" />
            <p className="text-lg">Feature 3 Description</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Features;
