// src/Hero.js
import React from 'react';
import './Hero.css';

const Hero = () => {
  return (
    <section className="hero">
      <div className="hero-content">
        <h1 className="hero-title">Welcome to Our SaaS Platform</h1>
        <p className="hero-subtitle">Effortlessly manage your business with our powerful tools.</p>
        <button className="hero-button">Get Started</button>
      </div>
      <div className="hero-image">
        <img src="your-image-url.jpg" alt="SaaS Platform" />
      </div>
    </section>
  );
};

export default Hero;
