import React from 'react';

const FeatureSection = ({ imgSrc, title, description, buttonText, buttonLink }) => {
  return (
    <section className="bg-gray-900 text-white py-20">
      <div className="container mx-auto flex flex-col md:flex-row items-center">
        <div className="md:w-1/2">
          <img src={imgSrc} alt={title} className="w-full h-auto"/>
        </div>
        <div className="md:w-1/2 md:pl-12 text-center md:text-left mt-8 md:mt-0">
          <h2 className="text-3xl font-bold mb-4">{title}</h2>
          <p className="text-lg mb-6">{description}</p>
          <a href={buttonLink} className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded-full transition duration-300 ease-in-out">
            {buttonText}
          </a>
        </div>
      </div>
    </section>
  );
}

export default FeatureSection;
