import React from 'react';

const FeaturesSection = () => {
  return (
    <section id="features" className="bg-gray-900 text-white py-20">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold mb-8">Features</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="p-4">
            <h3 className="text-xl font-bold mb-4">Centralized Portfolio</h3>
            <p className="text-lg mb-4">Create a single, streamlined webpage showcasing all your ventures.</p>
          </div>
          <div className="p-4">
            <h3 className="text-xl font-bold mb-4">Easy Sharing</h3>
            <p className="text-lg mb-4">Share your portfolio with a single link across all platforms.</p>
          </div>
          {/* Add more features */}
        </div>
      </div>
    </section>
  );
}

export default FeaturesSection;
