import React from 'react';

const HeroSection = () => {
  return (
    <section className="hero bg-black text-white py-20">
      <div className="container mx-auto text-center">
        <h1 className="text-4xl font-bold mb-4">Create Your Stunning Developer Portfolio</h1>
        <p className="text-lg mb-8">Empower your career with IndiePage.</p>
        <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-full transition duration-300 ease-in-out">Get Started</button>
      </div>
    </section>
  );
}

export default HeroSection;
