import React, { useState } from 'react';
import ProjectCard from './ProjectCard';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [projectInput, setProjectInput] = useState({
    name: '',
    description: '',
    url: '',
    revenueUsers: '',
    status: '',
    category: '',
  });

  const addProject = () => {
    if (projectInput.name.trim() !== '') {
      setProjects([...projects, projectInput]);
      setProjectInput({ name: '', description: '', url: '', revenueUsers: '', status: '', category: '' });
    }
  };

  const removeProject = (index) => {
    setProjects(projects.filter((_, i) => i !== index));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProjectInput({ ...projectInput, [name]: value });
  };

  return (
    <div className="bg-gray-800 p-6 rounded-md">
      <h2 className="text-2xl mb-4">Projects</h2>
      <form>
        <div className="mb-4">
          <label className="block mb-2">Name</label>
          <input
            type="text"
            name="name"
            value={projectInput.name}
            onChange={handleInputChange}
            className="w-full p-2 rounded-md bg-gray-700 border border-gray-600"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Description</label>
          <textarea
            name="description"
            value={projectInput.description}
            onChange={handleInputChange}
            className="w-full p-2 rounded-md bg-gray-700 border border-gray-600"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">URL</label>
          <input
            type="text"
            name="url"
            value={projectInput.url}
            onChange={handleInputChange}
            className="w-full p-2 rounded-md bg-gray-700 border border-gray-600"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Revenue/Users</label>
          <input
            type="text"
            name="revenueUsers"
            value={projectInput.revenueUsers}
            onChange={handleInputChange}
            className="w-full p-2 rounded-md bg-gray-700 border border-gray-600"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Status</label>
          <input
            type="text"
            name="status"
            value={projectInput.status}
            onChange={handleInputChange}
            className="w-full p-2 rounded-md bg-gray-700 border border-gray-600"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Category</label>
          <input
            type="text"
            name="category"
            value={projectInput.category}
            onChange={handleInputChange}
            className="w-full p-2 rounded-md bg-gray-700 border border-gray-600"
          />
        </div>
        <button
          type="button"
          onClick={addProject}
          className="w-full bg-green-600 p-2 rounded-md"
        >
          Add Project
        </button>
      </form>

      <div className="mt-6">
        {projects.map((project, index) => (
          <ProjectCard key={index} project={project} index={index} removeProject={removeProject} />
        ))}
      </div>
    </div>
  );
};

export default Projects;
