import React from 'react';

const Features = () => {
  return (
    <section className="bg-black text-white py-3">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 ">
        <h2 className="text-5xl font-bold mb-12 text-center">Our Features</h2>
        <div className="flex flex-col ">
          {/** Loop through features */}
          {[
            { image: "feature-image-1.jpg", description: "Feature 1 Description" },
            { image: "feature-image-2.jpg", description: "Feature 2 Description" },
            { image: "feature-image-3.jpg", description: "Feature 3 Description" },
          ].map((feature) => (
            <div key={feature.description} className="flex flex-col items-center mb-12"> {/* Added margin for spacing */}
              <div className="w-full flex justify-center">
                <img src={feature.image} alt={feature.description} className="rounded-lg shadow-lg" />
              </div>
              <div className="w-full text-center"> {/* Centered text */}
                <p className="text-lg">{feature.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
