import React, { useState } from 'react';
import SectionCard from './SectionCard';

const ProfileForm = () => {
  const [projects, setProjects] = useState([]);
  const [projectInput, setProjectInput] = useState('');

  const addProject = () => {
    if (projectInput.trim() !== '') {
      setProjects([...projects, projectInput]);
      setProjectInput('');
    }
  };

  const removeProject = (index) => {
    setProjects(projects.filter((_, i) => i !== index));
  };

  return (
    <div className="bg-gray-900 text-white p-6 rounded-md w-full md:w-3/4 lg:w-1/2">
      <h1 className="text-2xl mb-4">Profile</h1>
      <form>
        <div className="mb-4">
          <label className="block mb-2">Name</label>
          <input type="text" className="w-full p-2 rounded-md bg-gray-800 border border-gray-700" />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Bio</label>
          <input type="text" className="w-full p-2 rounded-md bg-gray-800 border border-gray-700" />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Location</label>
          <input type="text" className="w-full p-2 rounded-md bg-gray-800 border border-gray-700" />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Education</label>
          <input type="text" className="w-full p-2 rounded-md bg-gray-800 border border-gray-700" />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Work Experience</label>
          <input type="text" className="w-full p-2 rounded-md bg-gray-800 border border-gray-700" />
        </div>
        <div className="mb-4">
          <label className="block mb-2">Projects</label>
          <div className="flex items-center">
            <input
              type="text"
              value={projectInput}
              onChange={(e) => setProjectInput(e.target.value)}
              className="w-full p-2 rounded-md bg-gray-800 border border-gray-700"
            />
            <button type="button" onClick={addProject} className="ml-2 p-2 bg-pink-500 rounded-md">
              Add
            </button>
          </div>
          <ul className="mt-2">
            {projects.map((project, index) => (
              <SectionCard key={index} title={project} onRemove={() => removeProject(index)} />
            ))}
          </ul>
        </div>
      </form>
      <div className="mt-6">
        <button className="p-2 bg-pink-500 rounded-md">Save Profile</button>
      </div>
    </div>
  );
};

export default ProfileForm;
