import React from 'react';

const Features = () => {
  return (
    <section className="bg-black text-white py-3">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex flex-wrap justify-center"> {/* Added flex-wrap for horizontal stacking */}
        {/** Loop through features */}
        {[
          { image: "feature-image-1.jpg", description: "Feature 1 Description" },
          { image: "feature-image-2.jpg", description: "Feature 2 Description" },
          { image: "feature-image-3.jpg", description: "Feature 3 Description" },
        ].map((feature) => (
          <div key={feature.description} className="flex flex-row w-1/3 px-4 mb-12"> {/* Added w-1/3 for equal width, px-4 for padding */}
            <img src={feature.image} alt={feature.description} className="rounded-lg shadow-lg mr-4" /> {/* Added margin for spacing */}
            <div className="w-full"> {/* Full width for text container */}
              <p className="text-lg">{feature.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Features;
