// src/components/Hero.js
import React from 'react';

const Hero = () => {
  return (
    <section className="flex flex-col items-center justify-center h-screen bg-black text-white py-20">
      <div className="max-w-4xl mx-auto text-center">
        <h1 className="text-6xl font-bold mb-8 text-purple-500 font-heading">Welcome to Our SaaS Platform</h1>
        <p className="text-2xl mb-8 text-gray-400">Effortlessly manage your business with our powerful tools.</p>
        <button className="px-8 py-4 text-lg font-semibold text-black bg-purple-500 rounded-full hover:bg-purple-400 transition duration-300">
          Get Started
        </button>
      </div>
    </section>
  );
};

export default Hero;
